/* ==================================
 * Normalize & Hash (Manual SHA-256)
 * Compatible with n8n webhook JSON:
 * {
 *   "body": { "text": "..." },
 *   "headers": { ... },
 *   ...
 * }
 * ==================================
 */

// Manual SHA-256 implementation (no crypto import)
function sha256(msg) {
    function rightRotate(n, x) { return (x >>> n) | (x << (32 - n)); }
    function choice(x, y, z) { return (x & y) ^ (~x & z); }
    function majority(x, y, z) { return (x & y) ^ (x & z) ^ (y & z); }
    function sigma0(x) { return rightRotate(2, x) ^ rightRotate(13, x) ^ rightRotate(22, x); }
    function sigma1(x) { return rightRotate(6, x) ^ rightRotate(11, x) ^ rightRotate(25, x); }
    function gamma0(x) { return rightRotate(7, x) ^ rightRotate(18, x) ^ (x >>> 3); }
    function gamma1(x) { return rightRotate(17, x) ^ rightRotate(19, x) ^ (x >>> 10); }

    var K = [
      0x428a2f98,0x71374491,0xb5c0fbcf,0xe9b5dba5,0x3956c25b,0x59f111f1,0x923f82a4,0xab1c5ed5,
      0xd807aa98,0x12835b01,0x243185be,0x550c7dc3,0x72be5d74,0x80deb1fe,0x9bdc06a7,0xc19bf174,
      0xe49b69c1,0xefbe4786,0x0fc19dc6,0x240ca1cc,0x2de92c6f,0x4a7484aa,0x5cb0a9dc,0x76f988da,
      0x983e5152,0xa831c66d,0xb00327c8,0xbf597fc7,0xc6e00bf3,0xd5a79147,0x06ca6351,0x14292967,
      0x27b70a85,0x2e1b2138,0x4d2c6dfc,0x53380d13,0x650a7354,0x766a0abb,0x81c2c92e,0x92722c85,
      0xa2bfe8a1,0xa81a664b,0xc24b8b70,0xc76c51a3,0xd192e819,0xd6990624,0xf40e3585,0x106aa070,
      0x19a4c116,0x1e376c08,0x2748774c,0x34b0bcb5,0x391c0cb3,0x4ed8aa4a,0x5b9cca4f,0x682e6ff3,
      0x748f82ee,0x78a5636f,0x84c87814,0x8cc70208,0x90befffa,0xa4506ceb,0xbef9a3f7,0xc67178f2
    ];
    var H = [0x6a09e667,0xbb67ae85,0x3c6ef372,0xa54ff53a,0x510e527f,0x9b05688c,0x1f83d9ab,0x5be0cd19];

    msg = unescape(encodeURIComponent(msg)); // UTF-8
    var l = msg.length;
    var N = Math.ceil((l + 9) / 64);
    var M = new Array(N);

    for (var i = 0; i < N; i++) {
        M[i] = new Array(16);
        for (var j = 0; j < 16; j++) {
            var idx = i * 64 + j * 4;
            M[i][j] =
                ((msg.charCodeAt(idx) || 0) << 24) |
                ((msg.charCodeAt(idx + 1) || 0) << 16) |
                ((msg.charCodeAt(idx + 2) || 0) << 8) |
                ((msg.charCodeAt(idx + 3) || 0));
        }
    }

    // Padding
    M[N - 1][((l % 64) >> 2)] |= 0x80 << (24 - 8 * (l % 4));
    M[N - 1][14] = ((l * 8) / Math.pow(2, 32)) | 0;
    M[N - 1][15] = (l * 8) & 0xffffffff;

    var W = new Array(64);
    var a, b, c, d, e, f, g, h;
    for (var i = 0; i < N; i++) {
        for (var t = 0; t < 16; t++) W[t] = M[i][t];
        for (var t = 16; t < 64; t++) {
            W[t] = (gamma1(W[t - 2]) + W[t - 7] + gamma0(W[t - 15]) + W[t - 16]) & 0xffffffff;
        }
        a = H[0]; b = H[1]; c = H[2]; d = H[3]; e = H[4]; f = H[5]; g = H[6]; h = H[7];
        for (var t = 0; t < 64; t++) {
            var T1 = (h + sigma1(e) + choice(e, f, g) + K[t] + W[t]) & 0xffffffff;
            var T2 = (sigma0(a) + majority(a, b, c)) & 0xffffffff;
            h = g; g = f; f = e; e = (d + T1) & 0xffffffff;
            d = c; c = b; b = a; a = (T1 + T2) & 0xffffffff;
        }
        H[0] = (H[0] + a) & 0xffffffff;
        H[1] = (H[1] + b) & 0xffffffff;
        H[2] = (H[2] + c) & 0xffffffff;
        H[3] = (H[3] + d) & 0xffffffff;
        H[4] = (H[4] + e) & 0xffffffff;
        H[5] = (H[5] + f) & 0xffffffff;
        H[6] = (H[6] + g) & 0xffffffff;
        H[7] = (H[7] + h) & 0xffffffff;
    }

    function toHex(n) {
        let s = "";
        for (let i = 7; i >= 0; i--) s += ((n >>> (i * 4)) & 0xf).toString(16);
        return s;
    }

    return H.map(toHex).join("");
}

// ==================================
// MAIN EXECUTION
// ==================================
const rawText = $json.body?.text;

if (!rawText || typeof rawText !== "string") {
    return {
        error: true,
        message: "Expected JSON with body.text (string)",
        received_shape: $json
    };
}

// 1️⃣ Normalize
const normalized_text = rawText
    .toLowerCase()
    .normalize("NFD")
    .replace(/\p{Diacritic}/gu, "")
    .replace(/[.,!?;:"'’(){}\[\]<>]/g, "")
    .replace(/\s+/g, " ")
    .trim();

// 2️⃣ Hash
const idea_id = sha256(normalized_text);

// 3️⃣ Return
return {
    error: false,
    raw_text: rawText,
    normalized_text,
    idea_id,
    timestamp: new Date().toISOString(),
    is_duplicate: false,
    source: "webhook"
};

