// 1. Get all input items (The LLM result + The Search results)
const items = $input.all();

let ambiguityData = null;
const foundMatches = [];

// 2. Iterate through all items to separate Analysis from Matches
for (const item of items) {
  const data = item.json;

  // A. Check for LLM Analysis (it has the 'output' field from the Structured Output Parser)
  if (data.output) {
    ambiguityData = data.output;
  }

  // B. Check for Matches (they have a 'document' field from Qdrant)
  if (data.document) {
    foundMatches.push({
        score: data.score,
        document: data.document,
        id: data.id 
    });
  }
  // Note: We ignore the "No-match message" payload here. 
  // If that exists, 'foundMatches' stays empty [], which is exactly what we want.
}

// 3. Fallback if LLM failed for some reason
if (!ambiguityData) {
    ambiguityData = { ambiguity_index: 1.0, reasoning: "Analysis data missing" };
}

// 4. Return one clean object for the Webhook
return {
  ambiguity_analysis: ambiguityData,
  matches: foundMatches
};
